export const sendCartData = (cartData) => {
    return async (dispatch) => {
        const fetchData = () => {
            const res = fetch('https://redux-project-a5e9d-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json', {
                method: 'PUT',
                body: JSON.stringify(cartData)
            })
        }
        
        console.log(dispatch)
        fetchData();
    }
}
